                    }
                } else {
                    throw new JSONException("syntax error, unexpected value type");
                }
            }

            lexer.skipWhitespace();
            ch = lexer.getCurrent();

            if (ch == ',') {
                lexer.next();
                continue;
            } else if (ch == '}') {
                lexer.next();
                lexer.resetStringPosition();
                lexer.nextToken();

                this.setContext(context);

                return object;
            } else {
                throw new JSONException("syntax error, missing comma or closing brace");
            }
        }
