public final Object parseObject(final Map object, Object fieldName) {
    final JSONLexer lexer = this.lexer;

    if (lexer.token() == JSONToken.NULL) {
        lexer.nextToken();
        return null;
    }

    if (lexer.token() == JSONToken.RBRACE) {
        lexer.nextToken();
        return object;
    }

    if (lexer.token() == JSONToken.LITERAL_STRING && lexer.stringVal().length() == 0) {
        lexer.nextToken();
        return object;
    }

    if (lexer.token() != JSONToken.LBRACE && lexer.token() != JSONToken.COMMA) {
        throw new JSONException("syntax error, expect {, actual " + lexer.tokenName() + ", " + lexer.info());
    }

    ParseContext context = this.context;

    try {
        for (;;) {
            // Parsing logic here
            // Handle different cases for parsing JSON objects

            // Check if end of object
            if (lexer.token() == JSONToken.RBRACE) {
                lexer.nextToken();
                return object;
            }

            lexer.skipWhitespace();
            char ch = lexer.getCurrent();

            if (ch == ',') {
                lexer.next();
                continue;
            }

            // Handle key-value pairs
            Object key = parseKey();
            lexer.skipWhitespace();
            expect(lexer, ':');

            // Parse value based on the type
            Object value = parseValue();

            // Store key-value pair in the object map
            object.put(key, value);

            lexer.skipWhitespace();
            ch = lexer.getCurrent();

            if (ch == ',') {
                lexer.next();
            } else if (ch == '}') {
                lexer.next();
                return object;
            } else {
                throw new JSONException("syntax error");
            }
        }
    } finally {
        this.setContext(context);
    }
}

private Object parseKey() {
    Object key;
    char ch = lexer.getCurrent();

    if (ch == '"') {
        key = lexer.scanSymbol(symbolTable, '"');
    } else if (ch == '\'') {
        key = lexer.scanSymbol(symbolTable, '\'');
    } else {
        throw new JSONException("expect '\"' or '\'', but actual " + ch);
    }

    return key;
}

private Object parseValue() {
    char ch = lexer.getCurrent();

    if (ch == '{') {
        lexer.nextToken();
        Map<String, Object> innerObject = new HashMap<>();
        return parseObject(innerObject, null);
    } else if (ch == '[') {
        lexer.nextToken();
        List<Object> innerArray = new ArrayList<>();
        return parseArray(innerArray);
    } else {
        // Parse other value types like string, number, etc.
        return parseSingleValue();
    }
}

private void expect(JSONLexer lexer, char expect) {
    char ch = lexer.getCurrent();
    if (ch == expect) {
        lexer.next();
    } else {
        throw new JSONException("expect '" + expect + "', but actual " + ch);
    }
}

private Object parseSingleValue() {
    char ch = lexer.getCurrent();

    if (ch == '"') {
        return lexer.scanString();
    } else if (ch >= '0' && ch <= '9' || ch == '-') {
        lexer.scanNumber();
        return lexer.integerValue();
    } else {
        throw new JSONException("invalid value");
    }
}

private List<Object> parseArray(List<Object> array) {
    for (;;) {
        char ch = lexer.getCurrent();

        if (ch == ']') {
            lexer.nextToken();
            return array;
        }

        Object value = parseValue();
        array.add(value);

        ch = lexer.getCurrent();
        if (ch == ',') {
            lexer.next();
        } else if (ch == ']') {
            lexer.nextToken();
            return array;
        } else {
            throw new JSONException("syntax error");
        }
    }
}

