				char prev = '\0';
				int j = i;
				while (prev <= ' ' && --j >= 0) {
					prev = characters[j];
				}
				if (j < 0 || !Character.isLetterOrDigit(prev)) {
					inQuote = ch;
				}
			}
			continue;
		}

		if (inQuote != '\0') { //keep looping until the quote is closed.
			continue;
		}

		afterNewLine = false;

