public void update(final Sketch sketchIn) {
    if ((sketchIn == null) || sketchIn.isEmpty()) {
        // Null and empty input sketch does not change the state
        return;
    }

    Util.checkSeedHashes(seedHash_, sketchIn.getSeedHash());
    Sketch.checkSketchAndMemoryFlags(sketchIn);

    unionThetaLong_ = Math.min(unionThetaLong_, sketchIn.getThetaLong()); // Update union theta

    // Update unionEmpty_ flag based on the incoming sketch
    if (!sketchIn.isEmpty()) {
        unionEmpty_ = false;
    }

    final int curCountIn = sketchIn.getRetainedEntries(true);

    if (curCountIn > 0) {
        if (sketchIn.isOrdered() && sketchIn.hasMemory()) {
            // Process CompactSketch with Memory
            final Memory skMem = ((CompactSketch) sketchIn).getMemory();
            final int preambleLongs = skMem.getByte(PREAMBLE_LONGS_BYTE) & 0X3F;

            for (int i = 0; i < curCountIn; i++) {
                final int offsetBytes = (preambleLongs + i) << 3;
                final long hashIn = skMem.getLong(offsetBytes);

                if (hashIn >= unionThetaLong_) {
                    break; // Early stop
                }

                gadget_.hashUpdate(hashIn); // Update gadget
            }
        } else {
            // Process sketches on Java Heap or with array
            final long[] cacheIn = sketchIn.getCache();

            for (int i = 0; i < curCountIn; i++) {
                final long hashIn = cacheIn[i];

                if (hashIn >= unionThetaLong_) {
                    break; // Early stop
                }

                if (hashIn > 0) {
                    gadget_.hashUpdate(hashIn); // Update gadget
                }
            }
        }
    }

    unionThetaLong_ = Math.min(unionThetaLong_, gadget_.getThetaLong()); // Update union theta with gadget
}

