                    singleQuoteCount++;
                }

                if (i + 1 < length) {
                    char next = characters[i + 1];
                    if (Character.isLetterOrDigit(next) || (next <= ' ' && whitespaceRangeStart < next && next != '\n' && next != '\r')) { //no special characters after quote, might be escaping
