import org.springframework.core.ResolvableType;
import kotlin.reflect.KType;
import kotlin.reflect.jvm.ReflectJvmMapping;

private static boolean isAssignableFrom(Class<?> target, KType source) {
    // Convert the KType source to a Java Type
    Type parameterType = ReflectJvmMapping.getJavaType(source);

    // If the conversion to Type is null, we cannot determine assignability
    if (parameterType == null) {
        return false;
    }

    // Create ResolvableTypes for target and parameterType
    ResolvableType targetType = ResolvableType.forClass(target);
    ResolvableType sourceType = ResolvableType.forType(parameterType);

    // Check if the targetType is assignable from the sourceType
    return targetType.isAssignableFrom(sourceType);
}

