                        if (value instanceof Boolean) {
                            field.setBoolean(object, (Boolean) value);
                        } else if (value instanceof String) {
                            field.setBoolean(object, Boolean.parseBoolean((String) value));
                        }
                    } else if (fieldType == int.class) {
                        if (value instanceof Number) {
                            field.setInt(object, ((Number) value).intValue());
                        } else if (value instanceof String) {
                            field.setInt(object, Integer.parseInt((String) value));
                        }
                    } else if (fieldType == long.class) {
                        if (value instanceof Number) {
                            field.setLong(object, ((Number) value).longValue());
                        } else if (value instanceof String) {
                            field.setLong(object, Long.parseLong((String) value));
                        }
