                param = 0L;
            } else if (fieldClass == short.class) {
                param = (short) 0;
            } else if (fieldClass == byte.class) {
                param = (byte) 0;
            } else if (fieldClass == float.class) {
                param = 0.0f;
            } else if (fieldClass == double.class) {
                param = 0.0;
            } else if (fieldClass == char.class) {
                param = '\0';
            } else if (fieldClass == boolean.class) {
                param = false;
            }
            if (missFields == null) {
                missFields = new HashMap<String, Integer>();
            }
            missFields.put(fieldInfo.name, i);
        }
        params[i] = param;
    }

    if (missFields != null) {
        for (Map.Entry<String, Object> entry : map.entrySet()) {
            String key = entry.getKey();
            Object value = entry.getValue();

            FieldDeserializer fieldDeser = smartMatch(key);
            if (fieldDeser != null) {
                Integer index = missFields.get(fieldDeser.fieldInfo.name);
                if (index != null) {
                    params[index] = value;
                }
            }
        }
    }

    if (beanInfo.creatorConstructor != null) {
        try {
            object = beanInfo.creatorConstructor.newInstance(params);
        } catch (Exception e) {
            throw new JSONException("Failed to create instance using constructor", e);
        }
    } else if (beanInfo.factoryMethod != null) {
        try {
            object = beanInfo.factoryMethod.invoke(null, params);
        } catch (Exception e) {
            throw new JSONException("Failed to create instance using factory method", e);
        }
    }

    return object;
}

