                Integer previousCount = previous.get(symbol);
                Integer currentCount = current.get(symbol);

                if (previousCount == null && currentCount == null) {
                    toRemove.add(symbol);
                }

                if (previousCount == null || currentCount == null) {
                    continue;
                }

                increment(sums, symbol, Math.abs(previousCount - currentCount));
            }
        }
