@SuppressWarnings({ "unchecked", "rawtypes" })
public final Object parseObject(final Map object, Object fieldName) {
    final JSONLexer lexer = this.lexer;

    if (lexer.token() == JSONToken.NULL) {
        lexer.nextToken();
        return null;
    }

    if (lexer.token() == JSONToken.RBRACE) {
        lexer.nextToken();
        return object;
    }

    if (lexer.token() == JSONToken.LITERAL_STRING && lexer.stringVal().length() == 0) {
        lexer.nextToken();
        return object;
    }

    if (lexer.token() != JSONToken.LBRACE && lexer.token() != JSONToken.COMMA) {
        throw new JSONException("syntax error, expect {, actual " + lexer.tokenName() + ", " + lexer.info());
    }

    ParseContext context = this.context;
    try {
        Map map = object instanceof JSONObject ? ((JSONObject) object).getInnerMap() : object;

        boolean setContextFlag = false;
        for (;;) {
            lexer.skipWhitespace();
            char ch = lexer.getCurrent();
            if (lexer.isEnabled(Feature.AllowArbitraryCommas)) {
                while (ch == ',') {
                    lexer.next();
                    lexer.skipWhitespace();
                    ch = lexer.getCurrent();
                }
            }

            boolean isObjectKey = false;
            Object key;
            if (ch == '"') {
                key = lexer.scanSymbol(symbolTable, '"');
                lexer.skipWhitespace();
                ch = lexer.getCurrent();
                if (ch != ':') {
                    throw new JSONException("expect ':' at " + lexer.pos() + ", name " + key);
                }
            } else if (ch == '}') {
                lexer.next();
                lexer.resetStringPosition();
                lexer.nextToken();

                if (!setContextFlag) {
                    context = updateContextIfNeeded(object, fieldName, setContextFlag);
                    setContextFlag = true;
                }

                return object;
            } else if (ch == '\'') {
                if (!lexer.isEnabled(Feature.AllowSingleQuotes)) {
                    throw new JSONException("syntax error");
                }

                key = lexer.scanSymbol(symbolTable, '\'');
                lexer.skipWhitespace();
                ch = lexer.getCurrent();
                if (ch != ':') {
                    throw new JSONException("expect ':' at " + lexer.pos());
                }
            } else if (ch == EOI) {
                throw new JSONException("syntax error");
            } else if (ch == ',') {
                throw new JSONException("syntax error");
            } else if ((ch >= '0' && ch <= '9') || ch == '-') {
                lexer.resetStringPosition();
                lexer.scanNumber();
                key = parseNumberKey(lexer);
                ch = lexer.getCurrent();
                if (ch != ':') {
                    throw new JSONException("parse number key error" + lexer.info());
                }
            } else if (ch == '{' || ch == '[') {
                lexer.nextToken();
                key = parse();
                isObjectKey = true;
            } else {
                if (!lexer.isEnabled(Feature.AllowUnQuotedFieldNames)) {
                    throw new JSONException("syntax error");
                }

                key = lexer.scanSymbolUnQuoted(symbolTable);
                lexer.skipWhitespace();
                ch = lexer.getCurrent();
                if (ch != ':') {
                    throw new JSONException("expect ':' at " + lexer.pos() + ", actual " + ch);
                }
            }

            if (!isObjectKey) {
                lexer.next();
                lexer.skipWhitespace();
            }

            ch = lexer.getCurrent();

            lexer.resetStringPosition();

            if (key == JSON.DEFAULT_TYPE_KEY
                    && !lexer.isEnabled(Feature.DisableSpecialKeyDetect)) {
                String typeName = lexer.scanSymbol(symbolTable, '"');

                if (lexer.isEnabled(Feature.IgnoreAutoType)) {
                    continue;
                }

                Class<?> clazz = resolveClass(object, typeName);
                if (clazz == null) {
                    map.put(JSON.DEFAULT_TYPE_KEY, typeName);
                    continue;
                }

                return processTypeKey(object, fieldName, lexer, map, clazz);
            }

            if (key == "$ref"
                    && context != null
                    && !lexer.isEnabled(Feature.DisableSpecialKeyDetect)) {
                return processRefKey(context, lexer, map, key);
            }

            if (!setContextFlag) {
                context = updateContextIfNeeded(object, fieldName, setContextFlag);
                setContextFlag = true;
            }

            if (object.getClass() == JSONObject.class && key == null) {
                key = "null";
            }

            Object value = parseValue(lexer, ch, key, fieldName, context, map);

            lexer.skipWhitespace();
            ch = lexer.getCurrent();
            if (ch == ',') {
                lexer.next();
                continue;
            } else if (ch == '}') {
                lexer.next();
                lexer.resetStringPosition();
                lexer.nextToken();

                this.setContext(value, key);

                return object;
            } else {
                throw new JSONException("syntax error, position at " + lexer.pos() + ", name " + key);
            }

        }
    } finally {
        this.setContext(context);
    }
}

private Object parseNumberKey(JSONLexer lexer) {
    try {
        if (lexer.token() == JSONToken.LITERAL_INT) {
            return lexer.integerValue();
        } else {
            return lexer.decimalValue(true);
        }
    } catch (NumberFormatException e) {
        throw new JSONException("parse number key error" + lexer.info());
    }
}

private ParseContext updateContextIfNeeded(final Map object, Object fieldName, boolean setContextFlag) {
    ParseContext context = this.context;
    if (!setContextFlag) {
        if (this.context != null && fieldName == this.context.fieldName && object == this.context.object) {
            context = this.context;
        } else {
            ParseContext contextR = setContext(object, fieldName);
            if (context == null) {
                context = contextR;
            }
        }
    }
    return context;
}

private Class<?> resolveClass(final Map object, String typeName) {
    Class<?> clazz = null;
    if (object != null && object.getClass().getName().equals(typeName)) {
        clazz = object.getClass();
    } else {
        boolean allDigits = true;
        for (int i = 0; i < typeName.length(); ++i) {
            char c = typeName.charAt(i);
            if (c < '0' || c > '9') {
                allDigits = false;
                break;
            }
        }
        if (!allDigits) {
            clazz = config.checkAutoType(typeName, null, lexer.getFeatures());
        }
    }
    return clazz;
}

private Object processTypeKey(final Map object, Object fieldName, JSONLexer lexer, Map map, Class<?> clazz) {
    lexer.nextToken(JSONToken.COMMA);
    if (lexer.token() == JSONToken.RBRACE) {
        lexer.nextToken(JSONToken.COMMA);
        try {
            Object instance = createInstance(object, clazz);
            return instance;
        } catch (Exception e) {
            throw new JSONException("create instance error", e);
        }
    }

    this.setResolveStatus(TypeNameRedirect);

    if (this.context != null
            && fieldName != null
            && !(fieldName instanceof Integer)
            && !(this.context.fieldName instanceof Integer)) {
        this.popContext();
    }

    if (object.size() > 0) {
        Object newObj = TypeUtils.cast(object, clazz, this.config);
        this.setResolveStatus(NONE);
        this.parseObject(newObj);
        return newObj;
    }

    ObjectDeserializer deserializer = config.getDeserializer(clazz);
    Class deserClass = deserializer.getClass();
    if (JavaBeanDeserializer.class.isAssignableFrom(deserClass)
            && deserClass != JavaBeanDeserializer.class
            && deserClass != ThrowableDeserializer.class) {
        this.setResolveStatus(NONE);
    } else if (deserializer instanceof MapDeserializer) {
        this.setResolveStatus(NONE);
    }
    Object obj = deserializer.deserialze(this, clazz, fieldName);
    return obj;
}

private Object createInstance(final Map object, Class<?> clazz) throws Exception {
    Object instance = null;
    ObjectDeserializer deserializer = this.config.getDeserializer(clazz);
    if (deserializer instanceof JavaBeanDeserializer) {
        instance = TypeUtils.cast(object, clazz, this.config);
    }
    if (instance == null) {
        if (clazz == Cloneable.class) {
            instance = new HashMap();
        } else if ("java.util.Collections$EmptyMap".equals(clazz.getName())) {
            instance = Collections.emptyMap();
        } else if ("java.util.Collections$UnmodifiableMap".equals(clazz.getName())) {
            instance = Collections.unmodifiableMap(new HashMap());
        } else {
            instance = clazz.newInstance();
        }
    }
    return instance;
}

private Object processRefKey(ParseContext context, JSONLexer lexer, Map map, Object key) {
    lexer.nextToken(JSONToken.LITERAL_STRING);
    if (lexer.token() == JSONToken.LITERAL_STRING) {
        String ref = lexer.stringVal();
        lexer.nextToken(JSONToken.RBRACE);

        if (lexer.token() == JSONToken.COMMA) {
            map.put(key, ref);
            return null;
        }

        Object refValue = resolveReference(context, ref);

        if (lexer.token() != JSONToken.RBRACE) {
            throw new JSONException("syntax error, " + lexer.info());
        }
        lexer.nextToken(JSONToken.COMMA);

        return refValue;
    } else {
        throw new JSONException("illegal ref, " + JSONToken.name(lexer.token()));
    }
}

private Object resolveReference(ParseContext context, String ref) {
    Object refValue = null;
    if ("@".equals(ref)) {
        refValue = resolveAtSymbol(context);
    } else if ("..".equals(ref)) {
        refValue = resolveDotDotSymbol(context);
    } else if ("$".equals(ref)) {
        refValue = resolveDollarSymbol(context);
    } else {
        addResolveTask(new ResolveTask(context, ref));
        setResolveStatus(DefaultJSONParser.NeedToResolve);
    }
    return refValue;
}

private Object resolveAtSymbol(ParseContext context) {
    Object refValue = null;
    if (this.context != null) {
        ParseContext thisContext = this.context;
        Object thisObj = thisContext.object;
        if (thisObj instanceof Object[] || thisObj instanceof Collection<?>) {
            refValue = thisObj;
        } else if (thisContext.parent != null) {
            refValue = thisContext.parent.object;
        }
    }
    return refValue;
}

private Object resolveDotDotSymbol(ParseContext context) {
    Object refValue = null;
    if (context.object != null) {
        refValue = context.object;
    } else {
        addResolveTask(new ResolveTask(context, ".."));
        setResolveStatus(DefaultJSONParser.NeedToResolve);
    }
    return refValue;
}

private Object resolveDollarSymbol(ParseContext context) {
    Object refValue = null;
    ParseContext rootContext = context;
    while (rootContext.parent != null) {
        rootContext = rootContext.parent;
    }

    if (rootContext.object != null) {
        refValue = rootContext.object;
    } else {
        addResolveTask(new ResolveTask(rootContext, "$"));
        setResolveStatus(DefaultJSONParser.NeedToResolve);
    }
    return refValue;
}

private Object parseValue(JSONLexer lexer, char ch, Object key, Object fieldName, ParseContext context, Map map) {
    Object value;
    if (ch == '"') {
        value = parseStringValue(lexer, map, key);
    } else if (ch >= '0' && ch <= '9' || ch == '-') {
        value = parseNumberValue(lexer, map, key);
    } else if (ch == '[') {
        value = parseArrayValue(lexer, map, key, fieldName, context);
    } else if (ch == '{') {
        value = parseObjectValue(lexer, map, key, fieldName, context);
    } else {
        lexer.nextToken();
        value = parse();

        map.put(key, value);

        if (lexer.token() == JSONToken.RBRACE) {
            lexer.nextToken();
            return value;
        } else if (lexer.token() == JSONToken.COMMA) {
            return value;
        } else {
            throw new JSONException("syntax error, position at " + lexer.pos() + ", name " + key);
        }
    }
    return value;
}

private Object parseStringValue(JSONLexer lexer, Map map, Object key) {
    lexer.scanString();
    String strValue = lexer.stringVal();
    Object value = strValue;

    if (lexer.isEnabled(Feature.AllowISO8601DateFormat)) {
        JSONScanner iso8601Lexer = new JSONScanner(strValue);
        if (iso8601Lexer.scanISO8601DateIfMatch()) {
            value = iso8601Lexer.getCalendar().getTime();
        }
        iso8601Lexer.close();
    }

    map.put(key, value);
    return value;
}

private Object parseNumberValue(JSONLexer lexer, Map map, Object key) {
    Object value;
    lexer.scanNumber();
    if (lexer.token() == JSONToken.LITERAL_INT) {
        value = lexer.integerValue();
    } else {
        value = lexer.decimalValue(lexer.isEnabled(Feature.UseBigDecimal));
    }

    map.put(key, value);
    return value;
}

private Object parseArrayValue(JSONLexer lexer, Map map, Object key, Object fieldName, ParseContext context) {
    lexer.nextToken();

    JSONArray list = new JSONArray();

    final boolean parentIsArray = fieldName != null && fieldName.getClass() == Integer.class;

    if (fieldName == null) {
        this.setContext(context);
    }

    this.parseArray(list, key);

    Object value;
    if (lexer.isEnabled(Feature.UseObjectArray)) {
        value = list.toArray();
    } else {
        value = list;
    }
    map.put(key, value);

    if (lexer.token() == JSONToken.RBRACE) {
        lexer.nextToken();
        return value;
    } else if (lexer.token() == JSONToken.COMMA) {
        return value;
    } else {
        throw new JSONException("syntax error");
    }
}

private Object parseObjectValue(JSONLexer lexer, Map map, Object key, Object fieldName, ParseContext context) {
    lexer.nextToken();

    final boolean parentIsArray = fieldName != null && fieldName.getClass() == Integer.class;

    Map input = createMapBasedOnFeature(lexer);

    ParseContext ctxLocal = null;

    if (!parentIsArray) {
        ctxLocal = setContext(context, input, key);
    }

    Object obj = null;
    boolean objParsed = false;
    if (fieldTypeResolver != null) {
        String resolveFieldName = key != null ? key.toString() : null;
        Type fieldType = fieldTypeResolver.resolve(object, resolveFieldName);
        if (fieldType != null) {
            ObjectDeserializer fieldDeser = config.getDeserializer(fieldType);
            obj = fieldDeser.deserialze(this, fieldType, key);
            objParsed = true;
        }
    }
    if (!objParsed) {
        obj = this.parseObject(input, key);
    }

    if (ctxLocal != null && input != obj) {
        ctxLocal.object = object;
    }

    if (key != null) {
        checkMapResolve(object, key.toString());
    }

    map.put(key, obj);

    if (parentIsArray) {
        setContext(obj, key);
    }

    if (lexer.token() == JSONToken.RBRACE) {
        lexer.nextToken();

        setContext(context);
        return obj;
    } else if (lexer.token() == JSONToken.COMMA) {
        if (parentIsArray) {
            this.popContext();
        } else {
            this.setContext(context);
        }
        return obj;
    } else {
        throw new JSONException("syntax error, " + lexer.tokenName());
    }
}

private Map createMapBasedOnFeature(JSONLexer lexer) {
    Map input;
    if (lexer.isEnabled(Feature.CustomMapDeserializer)) {
        MapDeserializer mapDeserializer = (MapDeserializer) config.getDeserializer(Map.class);

        input = (lexer.getFeatures() & Feature.OrderedField.mask) != 0
                ? mapDeserializer.createMap(Map.class, lexer.getFeatures())
                : mapDeserializer.createMap(Map.class);
    } else {
        input = new JSONObject(lexer.isEnabled(Feature.OrderedField));
    }
    return input;
}

