        lexer.nextToken();
        JSONArray list = new JSONArray();
        this.parseArray(list, key);
        value = lexer.isEnabled(Feature.UseObjectArray) ? list.toArray() : list;
    } else if (ch == '{') {
        lexer.nextToken();
        Map input = lexer.isEnabled(Feature.CustomMapDeserializer)
                ? ((MapDeserializer) config.getDeserializer(Map.class)).createMap(Map.class, lexer.getFeatures())
                : new JSONObject(lexer.isEnabled(Feature.OrderedField));
        value = this.parseObject(input, key);
