private Object createInstance(Class<?> clazz, Map object, Object fieldName) throws JSONException {
    try {
        Object instance = null;
        ObjectDeserializer deserializer = this.config.getDeserializer(clazz);
        if (deserializer instanceof JavaBeanDeserializer) {
            instance = TypeUtils.cast(object, clazz, this.config);
        }

        if (instance == null) {
            if (clazz == Cloneable.class) {
                instance = new HashMap();
            } else {
                instance = clazz.newInstance();
            }
        }
        return instance;
    } catch (Exception e) {
        throw new JSONException("create instance error", e);
    }
}

private Object resolveReference(String ref, ParseContext context) {
    Object refValue = null;
    if ("@".equals(ref)) {
        if (this.context != null) {
            ParseContext thisContext = this.context;
            Object thisObj = thisContext.object;
            refValue = thisObj instanceof Object[] || thisObj instanceof Collection<?> ? thisObj : thisContext.parent.object;
        }
    } else if ("..".equals(ref)) {
        refValue = context.object != null ? context.object : addResolveTaskAndReturn(context, ref);
    } else if ("$".equals(ref)) {
        ParseContext rootContext = context;
        while (rootContext.parent != null) {
            rootContext = rootContext.parent;
        }
        refValue = rootContext.object != null ? rootContext.object : addResolveTaskAndReturn(rootContext, ref);
    } else {
        addResolveTask(new ResolveTask(context, ref));
        setResolveStatus(DefaultJSONParser.NeedToResolve);
    }
    return refValue;
}

private Object addResolveTaskAndReturn(ParseContext context, String ref) {
    addResolveTask(new ResolveTask(context, ref));
    setResolveStatus(DefaultJSONParser.NeedToResolve);
    return null;
}

