        }
        return strValue;
    } else if (ch >= '0' && ch <= '9' || ch == '-') {
        lexer.scanNumber();
        if (lexer.token() == JSONToken.LITERAL_INT) {
            return lexer.integerValue();
        } else {
            return lexer.decimalValue(lexer.isEnabled(Feature.UseBigDecimal));
        }
    } else if (ch == '[') {
        lexer.nextToken();
        JSONArray list = new JSONArray();
        this.parseArray(list, key);
        return lexer.isEnabled(Feature.UseObjectArray) ? list.toArray() : list;
    } else if (ch == '{') {
        lexer.nextToken();
        Map input = new JSONObject(lexer.isEnabled(Feature.OrderedField));
        return this.parseObject(input, key);
    } else {
        lexer.nextToken();
        return parse();
    }
}

private Object resolveReference(String ref, ParseContext context) {
    if ("@".equals(ref)) {
        return context.object;
    } else if ("..".equals(ref)) {
        return context.parent != null ? context.parent.object : null;
    } else if ("$".equals(ref)) {
        ParseContext rootContext = context;
        while (rootContext.parent != null) {
            rootContext = rootContext.parent;
        }
        return rootContext.object;
    } else {
        addResolveTask(new ResolveTask(context, ref));
        setResolveStatus(DefaultJSONParser.NeedToResolve);
        return null;
    }
