private Object parseValue(char ch, Object key) throws JSONException {
    final JSONLexer lexer = this.lexer;
    Object value = null;
    if (ch == '"') {
        lexer.scanString();
        value = lexer.stringVal();
        if (lexer.isEnabled(Feature.AllowISO8601DateFormat)) {
            JSONScanner iso8601Lexer = new JSONScanner((String) value);
            if (iso8601Lexer.scanISO8601DateIfMatch()) {
                value = iso8601Lexer.getCalendar().getTime();
            }
            iso8601Lexer.close();
        }
    } else if (ch >= '0' && ch <= '9' || ch == '-') {
        lexer.scanNumber();
        value = lexer.token() == JSONToken.LITERAL_INT ? lexer.integerValue() : lexer.decimalValue(lexer.isEnabled(Feature.UseBigDecimal));
    } else if (ch == '[') {
        lexer.nextToken();
        JSONArray list = new JSONArray();
        this.parseArray(list, key);
        value = lexer.isEnabled(Feature.UseObjectArray) ? list.toArray() : list;
    } else if (ch == '{') {
        lexer.nextToken();
        Map input = new JSONObject(lexer.isEnabled(Feature.OrderedField));
        value = this.parseObject(input, key);
    } else {
        lexer.nextToken();
        value = parse();
    }
    return value;
}

private Object resolveReference(String ref, ParseContext context) {
    Object refValue = null;
    if ("@".equals(ref)) {
        if (this.context != null) {
            ParseContext thisContext = this.context;
            refValue = thisContext.object;
            if (!(refValue instanceof Object[] || refValue instanceof Collection<?>) && thisContext.parent != null) {
                refValue = thisContext.parent.object;
            }
        }
    } else if ("..".equals(ref)) {
        refValue = context.object;
    } else if ("$".equals(ref)) {
        ParseContext rootContext = context;
        while (rootContext.parent != null) {
            rootContext = rootContext.parent;
        }
        refValue = rootContext.object;
    } else {
        addResolveTask(new ResolveTask(context, ref));
        setResolveStatus(DefaultJSONParser.NeedToResolve);
    }
    return refValue;
}

