                lexer.nextToken();
                return object;
            } else {
                throw new JSONException("syntax error, position at " + lexer.pos() + ", name " + key);
            }
        }
    } finally {
        this.setContext(context);
    }
}

private Object parseValue(char ch, Object key, Map map) {
    final JSONLexer lexer = this.lexer;
    Object value;

    if (ch == '"') {
        lexer.scanString();
        String strValue = lexer.stringVal();
        value = strValue;

        if (lexer.isEnabled(Feature.AllowISO8601DateFormat)) {
            JSONScanner iso8601Lexer = new JSONScanner(strValue);
            if (iso8601Lexer.scanISO8601DateIfMatch()) {
                value = iso8601Lexer.getCalendar().getTime();
            }
            iso8601Lexer.close();
        }
    } else if (ch >= '0' && ch <= '9' || ch == '-') {
        lexer.scanNumber();
        if (lexer.token() == JSONToken.LITERAL_INT) {
            value = lexer.integerValue();
        } else {
            value = lexer.decimalValue(lexer.isEnabled(Feature.UseBigDecimal));
        }
    } else if (ch == '[') {
        lexer.nextToken();
        JSONArray list = new JSONArray();
        this.parseArray(list, key);
        value = list;
    } else if (ch == '{') {
        lexer.nextToken();
        Map input = new JSONObject(lexer.isEnabled(Feature.OrderedField));
        value = this.parseObject(input, key);
    } else {
        lexer.nextToken();
        value = parse();
    }

    return value;
}

