public boolean scanFieldBoolean(char[] fieldName) {
    matchStat = UNKNOWN;

    if (!charArrayCompare(text, bp, fieldName)) {
        matchStat = NOT_MATCH_NAME;
        return false;
    }

    int index = bp + fieldName.length;

    char ch = charAt(index++);

    final boolean quote = ch == '"';
    if (quote) {
        ch = charAt(index++);
    }

    boolean value;
    if (ch == 't') {
        if (charAt(index++) != 'r' || charAt(index++) != 'u' || charAt(index++) != 'e') {
            matchStat = NOT_MATCH;
            return false;
        }
        value = true;
    } else if (ch == 'f') {
        if (charAt(index++) != 'a' || charAt(index++) != 'l' || charAt(index++) != 's' || charAt(index++) != 'e') {
            matchStat = NOT_MATCH;
            return false;
        }
        value = false;
    } else if (ch == '1') {
        value = true;
    } else if (ch == '0') {
        value = false;
    } else {
        matchStat = NOT_MATCH;
        return false;
    }

    if (quote && charAt(index++) != '"') {
        matchStat = NOT_MATCH;
        return false;
    }

    bp = index;
    ch = charAt(bp);

    for (;;) {
        if (ch == ',') {
            this.ch = charAt(++bp);
            matchStat = VALUE;
            token = JSONToken.COMMA;
            break;
        } else if (ch == '}') {
            ch = charAt(++bp);
            for (;;) {
                if (ch == ',') {
                    token = JSONToken.COMMA;
                    this.ch = charAt(++bp);
                } else if (ch == ']') {
                    token = JSONToken.RBRACKET;
                    this.ch = charAt(++bp);
                } else if (ch == '}') {
                    token = JSONToken.RBRACE;
                    this.ch = charAt(++bp);
                } else if (ch == EOI) {
                    token = JSONToken.EOF;
                } else if (isWhitespace(ch)) {
                    ch = charAt(++bp);
                    continue;
                } else {
                    matchStat = NOT_MATCH;
                    return false;
                }
                break;
            }
            matchStat = END;
            break;
        } else if (isWhitespace(ch)) {
            ch = charAt(++bp);
        } else {
            matchStat = NOT_MATCH;
            return false;
        }
    }

    return value;
}

