private void handleDefaultTypeKey(JSONLexer lexer, Map map, Object key) throws JSONException {
    String typeName = lexer.scanSymbol(symbolTable, '"');

    if (lexer.isEnabled(Feature.IgnoreAutoType)) {
        return;
    }

    Class<?> clazz = null;
    boolean allDigits = typeName.chars().allMatch(Character::isDigit);
    if (!allDigits) {
        clazz = config.checkAutoType(typeName, null, lexer.getFeatures());
    }

    if (clazz == null) {
        map.put(JSON.DEFAULT_TYPE_KEY, typeName);
        return;
    }

    lexer.nextToken(JSONToken.COMMA);
    if (lexer.token() == JSONToken.RBRACE) {
        lexer.nextToken(JSONToken.COMMA);
        try {
            Object instance = createInstance(clazz, map);
            if (instance != null) {
                return instance;
            }
        } catch (Exception e) {
            throw new JSONException("create instance error", e);
        }
    }

    this.setResolveStatus(TypeNameRedirect);

    if (this.context != null && !(this.context.fieldName instanceof Integer)) {
        this.popContext();
    }

    if (map.size() > 0) {
        Object newObj = TypeUtils.cast(map, clazz, this.config);
        this.setResolveStatus(NONE);
        this.parseObject(newObj);
        return newObj;
    }

    ObjectDeserializer deserializer = config.getDeserializer(clazz);
    Object obj = deserializer.deserialze(this, clazz, null);
    return obj;
}

private Object createInstance(Class<?> clazz, Map map) throws Exception {
    Object instance = null;
    ObjectDeserializer deserializer = this.config.getDeserializer(clazz);
    if (deserializer instanceof JavaBeanDeserializer) {
        instance = TypeUtils.cast(map, clazz, this.config);
    }

    if (instance == null) {
        if (clazz == Cloneable.class) {
            instance = new HashMap();
