            instance = Collections.emptyMap();
        } else if ("java.util.Collections$UnmodifiableMap".equals(clazz.getName())) {
            instance = Collections.unmodifiableMap(new HashMap());
        } else {
            instance = clazz.newInstance();
        }
    }

    return instance;
