        lexer.nextToken();
        JSONArray list = new JSONArray();
        this.parseArray(list, key);

        value = lexer.isEnabled(Feature.UseObjectArray) ? list.toArray() : list;
    } else if (ch == '{') {
        lexer.nextToken();
        Map<String, Object> input = new JSONObject(lexer.isEnabled(Feature.OrderedField));
        this.parseObject(input, key);

        value = input;
    } else {
        lexer.nextToken();
        value = parse();
    }

    return value;
