private Object resolveReference(String ref, ParseContext context) {
    if ("@".equals(ref)) {
        return this.context != null ? this.context.object : null;
    } else if ("..".equals(ref)) {
        return context.object != null ? context.object : null;
    } else if ("$".equals(ref)) {
        ParseContext rootContext = context;
        while (rootContext.parent != null) {
            rootContext = rootContext.parent;
        }
        return rootContext.object != null ? rootContext.object : null;
    } else {
        addResolveTask(new ResolveTask(context, ref));
        setResolveStatus(DefaultJSONParser.NeedToResolve);
        return null;
    }
}

private void handleEndOfObject(JSONLexer lexer, Map object) {
    if (lexer.token() == JSONToken.RBRACE) {
        lexer.nextToken();
    } else if (lexer.token() != JSONToken.COMMA) {
        throw new JSONException("syntax error");
    }
}
