        JSONScanner iso8601Lexer = new JSONScanner((String) value);
        if (iso8601Lexer.scanISO8601DateIfMatch()) {
            value = iso8601Lexer.getCalendar().getTime();
        }
        iso8601Lexer.close();
    }
    map.put(key, value);
}

private Object handleNumberValueParsing(JSONLexer lexer) {
    Object value;
    lexer.scanNumber();
    if (lexer.token() == JSONToken.LITERAL_INT) {
        value = lexer.integerValue();
    } else {
        value = lexer.decimalValue(lexer.isEnabled(Feature.UseBigDecimal));
    }
    return value;
}

private Object handleArrayParsing(JSONLexer lexer, ParseContext context, Object fieldName, Object key) {
    JSONArray list = new JSONArray();
    final boolean parentIsArray = fieldName != null && fieldName.getClass() == Integer.class;
    if (fieldName == null) {
        this.setContext(context);
    }
    this.parseArray(list, key);
    if (lexer.isEnabled(Feature.UseObjectArray)) {
        return list.toArray();
    }
    return list;
}

private Object handleObjectParsing(JSONLexer lexer, ParseContext context, Map map, Object key) {
    final boolean parentIsArray = fieldName != null && fieldName.getClass() == Integer.class;

    Map input = new JSONObject(lexer.isEnabled(Feature.OrderedField));
    ParseContext ctxLocal = null;
    if (!parentIsArray) {
        ctxLocal = setContext(context, input, key);
    }

    Object obj = this.parseObject(input, key);
    if (ctxLocal != null && input != obj) {
        ctxLocal.object = obj;
    }
    checkMapResolve(map, key.toString());
    return obj;
}

private Object resolveReference(String ref) {
    Object refValue = null;
    if ("@".equals(ref)) {
        refValue = resolveReferenceAt();
    } else if ("..".equals(ref)) {
        refValue = resolveReferenceDoubleDot();
    } else if ("$".equals(ref)) {
        refValue = resolveReferenceDollar();
    } else {
        addResolveTask(new ResolveTask(context, ref));
        setResolveStatus(DefaultJSONParser.NeedToResolve);
    }
    return refValue;
}

private Object resolveReferenceAt() {
    if (this.context != null) {
        ParseContext thisContext = this.context;
        Object thisObj = thisContext.object;
        if (thisObj instanceof Object[] || thisObj instanceof Collection<?>) {
            return thisObj;
        } else if (thisContext.parent != null) {
            return thisContext.parent.object;
        }
    }
    return null;
}

private Object resolveReferenceDoubleDot() {
    if (context.object != null) {
        return context.object;
    } else {
        addResolveTask(new ResolveTask(context, ".."));
        setResolveStatus(DefaultJSONParser.NeedToResolve);
        return null;
    }
}

private Object resolveReferenceDollar() {
    ParseContext rootContext = context;
    while (rootContext.parent != null) {
        rootContext = rootContext.parent;
    }
    if (rootContext.object != null) {
        return rootContext.object;
    } else {
        addResolveTask(new ResolveTask(rootContext, "$"));
        setResolveStatus(DefaultJSONParser.NeedToResolve);
        return null;
    }
}

